import React from 'react';
import './App.css';
import TodoMain from './components/TodoMain';

class App extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			pagelists:[true,false,false,false]
		}
		this.handlechangePage = this.handlechangePage.bind(this);
	}

	handlechangePage(e,index){
		let pagelists = this.state.pagelists;
		for (let i = 0; i < pagelists.length; i++) {
			i === index ?
				pagelists[i] = true:
				pagelists[i] = false			
		}
		this.setState({
			pagelists
		})
	}

	render(){
		let pagelists = this.state.pagelists;
		console.log(pagelists[0])
		return (
			<div  className="App">
			<div id="container">
				<div id="header">
					<div id="logo">
						<h1>Product management</h1>
					</div>
					<div id="menu">
						<ul>
							{ pagelists[0] ?
								<li onClick={(e) => this.handlechangePage(e, 0)} className = "currentpage">Homepage</li>
								:
								<li onClick={(e) => this.handlechangePage(e, 0)}>Homepage</li>					

							}
							{ pagelists[1] ?
								<li onClick={(e) => this.handlechangePage(e, 1)} className = "currentpage">Suppliers</li>
								:
								<li onClick={(e) => this.handlechangePage(e, 1)}>Suppliers</li>					

							}
							{ pagelists[2] ?
								<li onClick={(e) => this.handlechangePage(e, 2)} className = "currentpage">Categories</li>
								:
								<li onClick={(e) => this.handlechangePage(e, 2)}>Categories</li>					

							}
							{ pagelists[3] ?
								<li onClick={(e) => this.handlechangePage(e, 3)} className = "currentpage">Products</li>
								:
								<li onClick={(e) => this.handlechangePage(e, 3)}>Products</li>					

							}	
							<li>Log Out</li>
						</ul>
					</div>
				</div>
				i
				<TodoMain/>
			</div>
			</div>

		  );

	}
}


export default App;
